<?php

/**
 * AutoridadesTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class AutoridadesTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object AutoridadesTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Autoridades');
    }

    // Obtener obras sociales
    public static function obtenerAutoridades($estado=NULL)
    {
        $sql ="
SELECT aut.idautoridad, aut.nombre as autoridad, ent.nombre as entidad FROM autoridades aut JOIN cargo_autoridades ent ON aut.idcargoautoridad = ent.idcargoautoridad ";

		$sql .= " ORDER BY aut.idcargoautoridad ;";
        
        $q = Doctrine_Manager::getInstance()->getCurrentConnection()->fetchAssoc($sql);

        return $q;
    } 
}